      alignItems:'center',
      justifyContent:'center',
      margin:10,
    },
    tituloH1:{
      height:'5%',
      width:'100%',
      justifyContent:'center',
      margin:30,
      
    },
    escritoH2:{
      fontSize:25,
      fontWeight:'bold',
    },
    meio:{
      height: '40%',
      width: '100%',
      margin
